const mongoose = require('mongoose')

module.exports.STATUS_SCHEDULED = 'scheduled'
module.exports.STATUS_FINISHED = 'finished'
module.exports.STATUS_CANCELED = 'canceled'
module.exports.STATUS_CURRENT = 'current'

module.exports.CampaignSchema = new mongoose.Schema({
  name: String,
  month: Number,
  year: Number,
  monthDescription: String,
  dateInitial: Date,
  dateFinish: Date,
  painelByCoin: Number,
  limitByCoin: Number,
  sellerLimit: Number,
  managerLimit: Number,
  userPercentage: Number,
  sellerPercentage: Number,
  managerPercentage: Number,
  status: { type: String },
  exceptions: [{
    line: String,
    pattern: String,
    collectionName: String,
    painelByCoin: Number
  }],
  createdAt: {
    type: Date,
    default: new Date()
  },
  updatedAt: {
    type: Date,
    default: new Date()
  }
})